import re

def validate_sender_reference(sender_reference):
    # Define regular expressions for validation rules
    pattern = r'^[A-Za-z0-9]{1,16}$'  # 16 alphanumeric characters
    no_consecutive_slashes = r'[^/]{2,}'  # No consecutive slashes

    # Check if the sender_reference matches the format
    if re.match(pattern, sender_reference):
        # Check for network-validated rule: Must not start or end with '/'
        if not sender_reference.startswith('/') and not sender_reference.endswith('/'):
            # Check for network-validated rule: Must not contain two consecutive slashes
            if re.search(no_consecutive_slashes, sender_reference):
                return "Valid"
            else:
                return "Error: Sender's Reference contains two consecutive slashes ('//')"
        else:
            return "Error: Sender's Reference starts or ends with a slash ('/')"
    else:
        return "Error: Sender's Reference does not match the required format (16 alphanumeric characters)"
    # Test the validation function with an example
    result = validate_sender_reference(sender_reference)
    print(result)
